INSERT INTO `rzrk`.`rzrk_script_library` (`id`, `create_date`, `modify_date`, `name`, `script`) VALUES ('8a018b5443d16e6a0143d1f19ad40001', NULL, NULL, '[开始时间]不能大于[结束时间]', 'window.customEditorCheckList.push(function(){\r\n  var $beginDom = $(\"[field=\'开始日期\']\");\r\n    var $endDom = $(\"[field=\'结束日期\']\");\r\n  if($endDom.val() < $beginDom.val()){\r\n        window.top.$.messager.alert(\'错误\',\'开始时间不能大于结束时间\', \'error\');\r\n        return false;\r\n    }\r\n  return true;\r\n });');
INSERT INTO `rzrk`.`rzrk_script_library` (`id`, `create_date`, `modify_date`, `name`, `script`) VALUES ('8a018b5443d16e6a0143d1f19ad40002', NULL, NULL, '[部门]默认登录人', '$(function(){\r\n  if(window.isAddAction){\r\n//  setTimeout(function(){\r\n      $.get(\"/admin/admin!getLoginAdminDeparmentName.action\",function(depname){\r\n          $(\".contractCategory.cfield[field=\'部门\']\").each(function(index,node){\r\n              try{\r\n                  $(node).combotree(\"setValue\",depname);\r\n              }catch(e){\r\n                  $(node).val(depname);\r\n              }\r\n          });\r\n      });\r\n//  },2000);\r\n  }\r\n});');
