INSERT INTO `rzrk`.`rzrk_script_library` (`id`, `create_date`, `modify_date`, `name`, `script`) VALUES ('8a018b5443d16e6a0143d1f19ad40005', NULL, NULL, '[填表日期]默认今天', '$(function () {\r\n  if (window.isAddAction) {\r\n       var now = new Date();\r\n       var year = now.getFullYear(); //年\r\n       var month = now.getMonth() + 1; //月\r\n     var day = now.getDate(); //日\r\n\r\n        var clock = year + \"-\";\r\n\r\n       if (month < 10)\r\n         clock += \"0\";\r\n     clock += month + \"-\";\r\n\r\n     if (day < 10)\r\n           clock += \"0\";\r\n     clock += day + \" \";\r\n       try {\r\n           $(\".contractCategory.cfield[field=\'填表日期\']\").datebox(\'setValue\', clock);\r\n       } catch (e) {\r\n           $(\".contractCategory.cfield[field=\'填表日期\']\").val(clock);\r\n     }\r\n   }\r\n\r\n});\r\n');
